import React from "react";
import Country from "./Country";
import Loading from "./Loading";
import { useGlobalContext } from "../context";

const CountryList = () => {
  const { countries, loading, searchTerm, setSearchTerm, select, setSelect } =
    useGlobalContext();

  return loading ? (
    <section>
      <div className="">
        {countries
          .filter((country) => {
            if (select === "all" || select === "") {
              return country;
            } else if (select === country.region.toLowerCase()) {
              return country;
            }
          })
          .filter((country) => {
            if (searchTerm === "") {
              return country;
            } else if (
              country.name.common
                .toLowerCase()
                .includes(searchTerm.toLowerCase())
            ) {
              return country;
            }
          })
          .map((item, index) => {
            return <Country key={index} {...item} />;
          })}
      </div>
    </section>
  ) : (
    <Loading />
  );
};

export default CountryList;
