import React, { useContext, useEffect, useState } from "react";
import { Link, useParams } from "react-router-dom";
import { AppContext } from "../context";
import Loading from "../components/Loading";

const SingleCountry = () => {
  const countryList = [];
  const [Allcountry] = useContext(AppContext);
  const params = useParams();
  const currentCountry = params.country.replace("country-", "");
  const empty = "Error";
  const url = `https://restcountries.com/v3.1/name/${currentCountry}`;
  const [countries, setCountries] = useState([]);
  const [loading, setLoading] = useState(false);
  const [country, setcountry] = useState([]);

  useEffect(() => {
    fetch(URL, {
      method: "GET",
    })
      .then((response) => response.json())
      .then((data) => {
        console.log(data);
        const sortedCountry = data;
        setCountries(sortedCountry);
        setLoading(false);
      })
      .catch((error) => {
        console.error(error);
      });
  }, []);

  return !loading ? (
    <Loading />
  ) : (
    country.map((country) => <CountryDetail {...country} />)
  );
};

export default SingleCountry;

// const [

// ] = getCountry;

const CountryDetail = ({
  name: { common },
  flags,
  capital,
  population,
  region,
  subregion,
  languages,
  currencies,
  nativeName,
  borders,
  topLevelDomain,
}) => {
  const empty = "unknown";

  return (
    <div className="info-container">
      <Link to="/">
        <button>Back</button>
      </Link>
      <div className="country-detail-container">
        <div className="country-frag">
          <img src={flags.png} />
        </div>
        <div className="country-detail">
          <h3>{nativeName}</h3>
          <div className="detail">
            <div className="data">
              <h4>
                Native Name : <span>{nativeName}</span>
              </h4>
              <h4>
                Population : <span>{population}</span>
              </h4>
              <h4>
                Region : <span>{region}</span>
              </h4>
              <h4>
                Sub Region : <span>{subregion}</span>
              </h4>
              <h4>
                Capital: <span>{capital || ""}</span>
              </h4>
            </div>
            <div className="data">
              <h4>
                Top Level Domain : <span>{topLevelDomain}</span>
              </h4>
              <h4>
                Curriencies :{" "}
                <span>
                  {currencies
                    ? currencies
                        .map(
                          (currency) =>
                            `${currency.name} ( ${currency.symbol} )`
                        )
                        .join(", ")
                    : "not provided!"}
                </span>
              </h4>
              <h4>
                Languages :{" "}
                <span>{languages.map((language) => language.name)}</span>
              </h4>
            </div>
          </div>
          <div className="country-border ">
            <h3>Border Countries: </h3>

            <div className="country-border-button">
              {borders
                ? borders.map((border) => (
                    <button style={{ cursor: "initial" }}>{border}</button>
                  ))
                : "No border country."}
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};
