import React from "react";

const Filterbar = (props) => {
  const regions = ["All", "Africa", "Americas", "Asia", "Europe", "Oceania"];

  const fetchRegion = async (regionName) => {
    if (regionName === "All") {
      const URL = `https://restcountries.com/v3.1/all`;
      const response = await fetch(URL);
      const data = await response.json();
      props.setCountries(data);
    } else {
      const URL = `https://restcountries.com/v3.1/region/{regionName}`;
      const response = await fetch(URL);
      const data = await response.json();
      props.setCountries(data);
    }

    return (
      <div>
        <select name="Filter By Region" id="">
          <option value="none" hidden={true}>
            Filter by Region
          </option>
          <option value="all">All</option>
          {regions.map((region) => (
            <option key={region} value={region} onClick={fetchRegion(regions)}>
              {region}
            </option>
          ))}
        </select>
      </div>
    );
  };
};

export default Filterbar;
