import React from "react";

const Filterbar = (props) => {
  const regions = [
    {
      label: "All",
      name: "all",
    },
    {
      label: "Africa",
      name: "africa",
    },
    { label: "Americas", name: "americas" },
    {
      label: "Asia",
      name: "asia",
    },
    { label: "Europe", name: "europe" },
    { label: "Oceania", name: "oceania" },
  ];

  const fetchRegion = async (regionName) => {
    if (regionName === "all") {
      const url = `https://restcountries.com/v3.1/all`;
      const response = await fetch(url);
      const data = await response.json();
      props.setCountries(data);
    } else {
      const url = `https://restcountries.com/v3.1/region/${regionName}`;
      const response = await fetch(url);
      const data = await response.json();
      props.setCountries(data);
    }
  };

  return (
    <section>
      <div className="region-list">
        {regions.map((region) => (
          <li
            onClick={() => {
              fetchRegion(region.name);
            }}
            value={region.name}
            key={region.label}
          >
            {region.label}
          </li>
        ))}
      </div>
    </section>
  );
};
export default Filterbar;

